import { useDispatch, useSelector } from "react-redux";
import "./css/form.css";

import {socialCreator} from "../redux/actions/socialHandleAction";

import { useHistory } from "react-router";
import { useState } from "react";
let SocialHandle = () => {
    let history = useHistory();
    let dispatch = useDispatch();

    let social = useSelector(
        (state) => state.social
    );
    let[handleName,setHandleName]= useState(""); 
    let[handleLink,setHandleLink]= useState(""); 
    return (
        <>
            <div className="qual-container">
                <div className="qual-form">
                    <h2 className="m-4">Social Handle</h2>

                    <button
                        onClick={() => {
                            if(handleName!==""){
                                alert("First save this handle the add another");
                                return;
                            }
                            return <div class="row m-3 " >
                                <div class="col-10">
                                    <input
                                        type="text"
                                        class="form-control"
                                        placeholder="Enter Name of social Handle"
                                        value={handleName}
                                        onChange={(e) => {
                                            setHandleName(e.currentTarget.value);
                                        }}
                                    />
                                </div>
                                <div class="col-10">
                                    <input
                                        type="text"
                                        class="form-control"
                                        placeholder="Enter Link of social Handle"
                                        value={handleLink}
                                        onChange={(e) => {
                                            setHandleLink(e.currentTarget.value);
                                        }}
                                    />
                                </div>
                                <button
                                    onClick={(e) => {
                                        if(social.hasOwnProperty(handleName)){
                                            delete social[handleName]
                                            dispatch(socialCreator(social));
                                        }
                                        e.currentTarget.parentElement.remove();
                                    }}
                                    className="btn btn-secondary m-4">
                                    Delete This Handle
                                </button>
                                <button
                                    onClick={(e) => {
                                        dispatch(socialCreator({ ...social,handleName: handleLink }));
                                    }}
                                    className="btn btn-secondary m-4">
                                    Save This Handle
                                </button>
                            </div>
                        }}
                        className="btn btn-primary m-4">
                        Press The button
                    </button>
                    
                    <button
                        onClick={() => {
                            history.push("/personal");
                        }}
                        className="btn btn-primary m-4"
                    >
                        Back
                    </button>
                </div>
            </div>
            <button
                onClick={() => {
                    
                }}
                className="btn btn-primary qual-gen"
            >
                Generate link
            </button>
        </>
    );
};

export default SocialHandle;
