import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { useHistory } from "react-router-dom";
import userImageActions from "../redux/actions/userImageActions"
import Hero from "./hero"
import "./css/style.css"
let ImageUploading = () => {


 let history=useHistory();
//  let state= useSelector(state=>state.userImage);
//  console.log(state);
  let dispatch= useDispatch();
  let onImageChange = event => {
    if (event.target.files && event.target.files[0]) {
      let img = event.target.files[0];
      dispatch(userImageActions(URL.createObjectURL(img)));
    }
  };

  let details = useSelector((state) => state.details);

  let { fname,oneWord} = details;
  return (
      <>
      <div className="user-image" style={{width:"50vw",height: "calc(100vh - 56px)"}}>
        <div style={{ width:"100%" , height:"100%",display:"flex" , alignItems:"center", justifyContent:"center", flexDirection:"column"}}>

        <div class="row m-3">
            <div class="col-10 mt-2">
              <input
                type="text"
                class="form-control"
                placeholder="Enter the technology you are working on"
                value={tech}
                onChange={(e) => {
                  dispatch(detailCreator({ tech: e.currentTarget.value }));
                }}
              />
            </div>
            <div class="col-10 mt-2">
              <input
                value={email}
                onChange={(e) => {
                  dispatch(detailCreator({ email: e.currentTarget.value }));
                }}
                type="email"
                class="form-control"
                placeholder="Email"
              />
            </div>
          </div>
          <h1 className="bg-primary m-5">Select Your Image</h1>
          <input type="file" name="myImage" onChange={onImageChange} />
        <button
            onClick={() => {
              history.push("/personal");
            }}
            className="btn btn-primary m-5"
          >
            Next
       </button>
       </div>
      </div>
      <Hero />
      </>
  );
}
export default ImageUploading;
