import Preview from "./preview";
import "./css/form.css";
import { useDispatch, useSelector } from "react-redux";
import { detailCreator } from "../redux/actions/detailsActions";
import { useHistory } from "react-router-dom";
let PersonalData = () => {
  let details = useSelector((state) => state.details);

  let { fname, lname, email, phone, city, state } = details;

  let history = useHistory();

  let dispatch = useDispatch();

  return (
    <>
      <div className="personal-container">
        <div className="personal-form">
          <h2 className="m-4">Personal Details</h2>
          <div class="row m-3">
          <textarea placeholder={"Enter a brief description about you"} className="m-3" />
            <div class="col-5">
              <input
                type="text"
                class="form-control"
                placeholder="First name"
                value={fname}
                onChange={(e) => {
                  dispatch(detailCreator({ fname: e.currentTarget.value }));
                }}
              />
            </div>
            <div class="col-5">
              <input
                value={lname}
                onChange={(e) => {
                  dispatch(detailCreator({ lname: e.currentTarget.value }));
                }}
                type="text"
                class="form-control"
                placeholder="Last name"
              />
            </div>

            <div class="col-5">
              <input
                value={email}
                onChange={(e) => {
                  dispatch(detailCreator({ email: e.currentTarget.value }));
                }}
                type="email"
                class="form-control"
                placeholder="Email"
              />
            </div>
            <div class="col-5">
              <input
                value={phone}
                onChange={(e) => {
                  dispatch(detailCreator({ phone: e.currentTarget.value }));
                }}
                type="number"
                class="form-control"
                placeholder="Phone"
              />
            </div>

            <div class="col-5">
              <input
                value={city}
                onChange={(e) => {
                  dispatch(detailCreator({ city: e.currentTarget.value }));
                }}
                type="text"
                class="form-control"
                placeholder="City"
              />
            </div>

            <div class="col-5">
              <input
                value={state}
                onChange={(e) => {
                  dispatch(detailCreator({ state: e.currentTarget.value }));
                }}
                type="text"
                class="form-control"
                placeholder="State"
              />
            </div>
            <div class="col-5">
              <input
                value={birthday}
                onChange={(e) => {
                  dispatch(detailCreator({ birthday: e.currentTarget.value }));
                }}
                type="text"
                class="form-control"
                placeholder="Birthday"
              />
            </div>

            <div class="col-5">
              <input
                value={age}
                onChange={(e) => {
                  dispatch(detailCreator({ age: e.currentTarget.value }));
                }}
                type="text"
                class="form-control"
                placeholder="Age"
              />
            </div>
          </div>

          <textarea
           placeholder={"Enter a brief description about you"} 
           value={description}

           className="m-3" />
          <button
            onClick={() => {
              history.push("/qualifications");
            }}
            className="btn btn-primary m-4"
          >
            Next
          </button>
        </div>
        <Preview />
      </div>
    </>
  );
};

export default PersonalData;
